function y=fileExistCheck(flist,outputFile)% outputFile should end with a number, then it will automatically create a new file with% the next index.[outputFileBody,outputFileExt]=filenamediv(outputFile);len=length(outputFileBody);for n=1:length(flist)		%if strcmp(flist(n).name,outputFile)     %if number already present, increment	if strcmpi(flist(n).name,outputFile)     %IMPT CHANGE: Now CASE INSENSITIVE - by LR 1/16/09          indnum=strfind(outputFileBody,'_');          indnum=indnum(end);          outputFile=[outputFileBody(1:indnum) ...              num2str(str2num(outputFileBody(indnum+1:end))+1) ...         	  '.' outputFileExt];                 outputFile=fileExistCheck(flist,outputFile);	endendy=outputFile;